package org.com.allen.enhance.basic.jvm;public class ClassLoadDemo1 {    public static void main(String[] args) throws ClassNotFoundException {        System.out.println(System.getProperty("java.class.path"));        Class<?> aClass = Class.forName("org.com.allen.enhance.basic.jvm.TestBean");        System.out.println(aClass.getClassLoader());        System.out.println(aClass.getClassLoader().getParent());        System.out.println(aClass.getClassLoader().getParent().getParent());    }}